INSERT INTO `CONTENT` (`id`, `title`, `summary`, `description`, `keyword`, `status`, `meta_description`, `meta_keyword`, `page_title`, `id_user`, `publish_date`, `insert_date`, `delete_date`) VALUES (1, 'neme-sys - Content Management System (CMS)', '&ldquo;<em>Dove c`&egrave; una grande volont&agrave; non possono esserci grandi difficolt&agrave;</em>!&rdquo;<br />\r\n(Niccol&ograve; Machiavelli)<br />\r\n<br />\r\n<img width="480" border="0" align="top" hspace="0" height="205" alt="" src="/public/upload/wysiwyg_editor/content-office.jpg" /><br />\r\n<br />\r\n<strong>neme-sys </strong>&egrave; un Content Management System (CMS) che permette di creare il proprio portale web e di gestirne i contenuti con estrema semplicit&agrave;.<br />\r\n<br />\r\nSviluppato con tecnologia <strong>ASP</strong>, permette di essere installato e configurato in pochi minuti e <strong>non necessita di alcuna libreria o componente esterno</strong> per il suo funzionamento.<br />\r\n<br />\r\nRicco di tutte le funzionalit&agrave; necessarie per un CMS professionale, <strong>neme-sys </strong>&egrave; stato sviluppato con l`utente in mente.<br />\r\n<br />\r\nL`interfaccia di amministrazione &egrave; <strong>molto intuitiva</strong> ed &egrave; stata <strong>progettata per guidare l</strong>`<strong>utente</strong> attraverso le varie fasi di creazione e manutenzione del proprio sito.<br />\r\n<br />\r\n<strong>neme-sys </strong>oltre ad essere un versatile CMS, integra un sistema di <strong>E-commerce di classe enterprise (econeme-sys)</strong>, completo di ogni funzionalit&agrave; necessaria a chi intenda sviluppare il proprio commercio online.', '', '', 1, '', '', '', 1, '2013-05-28 14:28:00', '2013-05-28 14:28:47', '9999-12-31 23:59:00');
