INSERT INTO `MULTI_LANGUAGES` (`keyword`, `lang_code`, `value`) VALUES('backend.prodotti.detail.table.label.field_help_desc', 'EN', 'to obtain a description of the <b>field</b> in different languages, create in the <b>Multilanguage management</b> three new composite keys as follows: <br/> for frontend: frontend.prodotto.field.label.xxxxx <br/> for the backoffice pages: backend.prodotti.detail.table.label.xxxxx<br/>for the send e-mail templates: backend.prodotti.mail.label.xxxxx<br/><br/> where xxxxx is the value entered in the <b>description</b> field');
