INSERT INTO `multi_languages` (`keyword`, `lang_code`, `value`) VALUES('backend.utenti.detail.table.label.field_help_desc', 'EN', 'to obtain a description of the <b>field</b> in different languages, create in the <b>Multilanguage management</b> three new composite keys as follows: <br/> for frontend: frontend.area_user.manage.label.xxxxx <br/> for the backoffice pages: backend.utenti.detail.table.label.xxxxx<br/>for the send e-mail templates: backend.utenti.mail.label.xxxxx<br/><br/> where xxxxx is the value entered in the <b>description</b> field');
INSERT INTO `multi_languages` (`keyword`, `lang_code`, `value`) VALUES('backend.utenti.detail.table.label.group_help_desc', 'EN', 'to obtain a description of the <b>group</b> in different languages, create in the <b>Multilanguage management</b> two new composite keys as follows: <br/> for frontend: frontend.area_user.manage.label.group.xxxxx <br/> for the backoffice pages: backend.utenti.detail.table.label.group.xxxxx <br/> <br/> where xxxxx is the value entered in the <b>Membership group</b> field');
INSERT INTO `multi_languages` (`keyword`, `lang_code`, `value`) VALUES('backend.utenti.detail.table.label.field_help_desc_values', 'IT', '&egrave; possibile definire una lista di valori da utilizzare per i campi di tipo select, select-multiple, checkbox e radio, specificando ogni valore separato da virgola;<br/><br/>per ottenere la lista dei <b>valori</b> in multilingua, creare nella sezione <b>Gestione label multilingua</b> una nuova chiave composta nel modo seguente: portal.commons.user_field.label.xxxxx<br/><br/>dove xxxxx corrisponde ad ogni singolo elemento inserito nel campo <b>Valori</b>');
